import React, { useState } from "react";
import axios from "axios";
import ImageCard from "./ImageCard";
import ImageList from "./ImageList";
import LoadingSpinner from "./LoadingSpinner";
import env from "react-dotenv";

const Search = () => {
  const [query, setQuery] = useState("");
  const [data, setData] = useState([]);

  const [searchStatus, setSearchStatus] = useState(false);
  const [imageListStatus, setImageListStatus] = useState(true);
  const [isLoading, setIsLoading] = useState(false);

  const searchImage = async (e) => {
    e.preventDefault();
    setImageListStatus(false);
    setSearchStatus(true);
    setIsLoading(true);
    await axios
      .get(
        `https://api.unsplash.com/search/photos?query=${query}&per_page=100`,
        {
          headers: {
            Authorization: `Client-ID -Gp9A6SdZAqZdZxDX6AQj11D881e9ppbEhAWRndzwjs`,
          },
        }
      )
      .then((response) => {
        setData(response.data.results);
        setIsLoading(false);
      });
  };

  return (
    <>
      <form>
        <label
          for="default-search"
          className="mb-2 text-sm font-medium text-gray-900 sr-only dark:text-white"
        >
          Search
        </label>
        <div className="mt-10 -mb-20 ml-auto mr-auto w-6/12 relative">
          <div className="absolute inset-y-0 left-0 flex items-center pl-3 pointer-events-none">
            <svg
              className="w-4 h-4 text-gray-500 dark:text-gray-400"
              aria-hidden="true"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 20 20"
            >
              <path
                stroke="currentColor"
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="m19 19-4-4m0-7A7 7 0 1 1 1 8a7 7 0 0 1 14 0Z"
              />
            </svg>
          </div>
          <input
            type="search"
            id="default-search"
            className="block w-full p-4 pl-10 text-sm text-gray-900 border border-gray-300 rounded-lg bg-gray-50 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
            placeholder="Search images"
            name="query"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            required
          />
          <button
            type="submit"
            className="text-white absolute right-2.5 bottom-2.5 bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-4 py-2 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
            onClick={searchImage}
            disabled={isLoading}
          >
            Search
          </button>
        </div>
      </form>

      {imageListStatus && <ImageList />}
      {isLoading ? LoadingSpinner : searchStatus && <ImageCard data={data} />}
    </>
  );
};

export default Search;
