import React, { useEffect, useState } from "react";
import axios from "axios";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Modal from "@mui/material/Modal";

const ImageCard = () => {
  const getRandomPageNumber = Math.floor(Math.random() * 1000) + 1;
  const API_KEY = "Fo60-wymwVPkYJTYL-dLfcboG54c1nWlaH7USGWddHU";

  const [data, setData] = useState([]);
  useEffect(() => {
    const getImages = async () => {
      const getAllImages = await axios.get(
        `https://api.unsplash.com/photos?page=${getRandomPageNumber}&per_page=100`,
        {
          headers: {
            Authorization: `Client-ID ${API_KEY}`,
          },
        }
      );
      //console.log(getAllImages.data);
      setData(getAllImages.data);
    };
    getImages();
  }, []);

  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 400,
    bgcolor: "background.paper",
    border: "2px solid #000",
    boxShadow: 24,
    p: 4,
  };

  return (
    <>
      <div className="mt-1 grid grid-cols-1 gap-x-6 gap-y-10 sm:grid-cols-2 lg:grid-cols-4 xl:gap-x-8">
        {data.map((image) => (
          <div
            key={image.id}
            className="group relative rounded-lg p-3 shadow-[0_3px_10px_rgb(0,0,0,0.2)]"
          >
            <div className="aspect-h-1 aspect-w-1 w-full overflow-hidden rounded-md bg-gray-200 lg:aspect-none group-hover:opacity-75 lg:h-80">
              <img
                src={image.urls.regular}
                alt={image.alt_description}
                className="h-full w-full object-cover object-center lg:h-full lg:w-full"
                onClick={handleOpen}
              />
            </div>

            <div className="mt-4 flex justify-between">
              <div>
                <img
                  className="h-8 w-8 rounded-full"
                  src={image.user.profile_image.small}
                  alt=""
                />
                <h3 className="text-sm text-gray-700">{image.user.name}</h3>
                <h3 className="text-sm text-gray-700">
                  @{image.user.instagram_username}
                </h3>
              </div>

              <p className="text-sm font-medium text-gray-900">
                <img
                  src="https://d3sxshmncs10te.cloudfront.net/icon/free/svg/1853251.svg?token=eyJhbGciOiJoczI1NiIsImtpZCI6ImRlZmF1bHQifQ__.eyJpc3MiOiJkM3N4c2htbmNzMTB0ZS5jbG91ZGZyb250Lm5ldCIsImV4cCI6MTY5Nzc2MDAwMCwicSI6bnVsbCwiaWF0IjoxNjk3NTI3MzI5fQ__.178561ed00c7417a457c9cbce1fe14a09e299d433f35f6d692acf8c23b9d3feb"
                  alt="like"
                  style={{ height: "20px", width: "20px" }}
                />
                {image.likes}
              </p>
            </div>
            <Modal
              show={props.openModal === "default"}
              onClose={() => props.setOpenModal(undefined)}
            >
              <Modal.Header>Terms of Service</Modal.Header>
              <Modal.Body>
                <div className="space-y-6">
                  <p className="text-base leading-relaxed text-gray-500 dark:text-gray-400">
                    With less than a month to go before the European Union
                    enacts new consumer privacy laws for its citizens, companies
                    around the world are updating their terms of service
                    agreements to comply.
                  </p>
                  <p className="text-base leading-relaxed text-gray-500 dark:text-gray-400">
                    The European Unionâ€™s General Data Protection Regulation
                    (G.D.P.R.) goes into effect on May 25 and is meant to ensure
                    a common set of data rights in the European Union. It
                    requires organizations to notify users as soon as possible
                    of high-risk data breaches that could personally affect
                    them.
                  </p>
                </div>
              </Modal.Body>

            </Modal>
          </div>
        ))}
      </div>
    </>
  );
};

export default ImageCard;
